mapper.insert:

INSERT INTO photo_table (photo_num, mem_id, photo_content, photo_date, photo_updatedate, photo_status) VALUES (
	  photo_num
	, #{mem_id}
	, #{photo_content}
	, NOW()
	, NOW()
	, 'N'
)

mapper.update:
UPDATE photo_table
SET
 	  photo_content = #{photo_content}
	, photo_updatedate = NOW()
WHERE
	photo_num = #{photo_num}
